def __unicode__(self):
    return self.description
def all(cls):
    return cls.objects.all()
def filter_by_address(cls, address):
    return cls.objects.filter(address__contains=address)

def filter_by_line_number(cls, line_number):
    api = BuslineAPI()
    try:
        objects = api.filter_by_line(line_number)
    except:
        objects = cls.objects.filter(line_number__startswith=line_number)
    return objects

def _terminal_json_to_object(self, terminal_json):
    terminal = Terminal()
    for attribute in terminal_json.keys():
        setattr(terminal, attribute, terminal_json[attribute])
    return terminal
